// App.jsx
import React, { useState } from "react";
import axios from "axios";

export default function App() {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hello! I am your Campus Helpdesk Bot. How can I help you today?" }
  ]);
  const [input, setInput] = useState("");

  const sendMessage = async () => {
    if (!input.trim()) return;

    // Add user message
    const userMessage = { sender: "user", text: input };
    setMessages((prev) => [...prev, userMessage]);

    try {
      const response = await axios.post("http://127.0.0.1:5000/api/chat", {
        message: input
      });

      let botText = response.data.reply;

      // Optional: format JSON-like data (timetable, syllabus, hostel)
      if (response.data.source === "university-data") {
        // Replace \n with <br/> for line breaks in HTML
        botText = botText.split("\n").map((line, index) => (
          <span key={index}>
            {line}
            <br />
          </span>
        ));
      }

      const botMessage = { sender: "bot", text: botText };
      setMessages((prev) => [...prev, botMessage]);
    } catch (err) {
      const errorMsg = {
        sender: "bot",
        text: "Sorry, something went wrong. Please try again later."
      };
      setMessages((prev) => [...prev, errorMsg]);
    }

    setInput("");
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      sendMessage();
    }
  };

  return (
    <div className="w-full max-w-xl mx-auto mt-10 p-4 border rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold mb-4 text-center">Campus Helpdesk Chatbot</h1>
      <div className="h-96 overflow-y-auto border p-2 mb-2 rounded">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-2 p-2 rounded ${
              msg.sender === "bot" ? "bg-gray-200 text-black" : "bg-blue-500 text-white text-right"
            }`}
          >
            {msg.text}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="Type your message..."
        className="w-full p-2 border rounded mb-2"
      />
      <button
        onClick={sendMessage}
        className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
      >
        Send
      </button>
    </div>
  );
}
