import React, { useState, useRef, useEffect } from "react";
import { Moon, Sun, History, X, Trash2 } from "lucide-react";

function App() {
  const [messages, setMessages] = useState([
    { 
      sender: "bot", 
      text: "Hello! I'm your Campus Helpdesk Assistant. I can help you with timetables, faculty information, fees, hostel details, library hours, and more. How can I assist you today?",
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [showHistory, setShowHistory] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [messages]);

  useEffect(() => {
    const saved = localStorage.getItem('chatHistory');
    if (saved) {
      try {
        setChatHistory(JSON.parse(saved));
      } catch (e) {
        console.error('Failed to load history');
      }
    }
  }, []);

  const saveToHistory = (userMsg, botMsg) => {
    const historyItem = {
      id: Date.now(),
      user: userMsg,
      bot: botMsg,
      timestamp: new Date().toISOString()
    };
    const newHistory = [historyItem, ...chatHistory].slice(0, 50);
    setChatHistory(newHistory);
    localStorage.setItem('chatHistory', JSON.stringify(newHistory));
  };

  const clearHistory = () => {
    setChatHistory([]);
    localStorage.removeItem('chatHistory');
  };

  const loadHistoryItem = (item) => {
    setMessages([
      { sender: "bot", text: "Hello! I'm your Campus Helpdesk Assistant. How can I assist you today?", timestamp: new Date() },
      { sender: "user", text: item.user, timestamp: new Date(item.timestamp) },
      { sender: "bot", text: item.bot, timestamp: new Date(item.timestamp) }
    ]);
    setShowHistory(false);
  };

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: "user", text: input, timestamp: new Date() };
    setMessages(prev => [...prev, userMessage]);
    const userText = input;
    setInput("");
    setIsTyping(true);

    try {
      const response = await fetch("http://127.0.0.1:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: userText })
      });
      const data = await response.json();

      setTimeout(() => {
        const botMessage = { sender: "bot", text: data.reply, timestamp: new Date() };
        setMessages(prev => [...prev, botMessage]);
        setIsTyping(false);
        saveToHistory(userText, data.reply);
      }, 500);
    } catch (error) {
      setTimeout(() => {
        const errorMessage = { 
          sender: "bot", 
          text: "Sorry, I'm having trouble connecting. Please try again later.",
          timestamp: new Date()
        };
        setMessages(prev => [...prev, errorMessage]);
        setIsTyping(false);
      }, 500);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const quickActions = [
    "View my timetable",
    "Faculty information",
    "Check fees",
    "Library hours"
  ];

  const handleQuickAction = async (action) => {
    const userMessage = { sender: "user", text: action, timestamp: new Date() };
    setMessages(prev => [...prev, userMessage]);
    setIsTyping(true);

    try {
      const response = await fetch("http://127.0.0.1:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: action })
      });
      const data = await response.json();

      setTimeout(() => {
        const botMessage = { sender: "bot", text: data.reply, timestamp: new Date() };
        setMessages(prev => [...prev, botMessage]);
        setIsTyping(false);
        saveToHistory(action, data.reply);
      }, 500);
    } catch (error) {
      setTimeout(() => {
        const errorMessage = { 
          sender: "bot", 
          text: "Sorry, I'm having trouble connecting. Please try again later.",
          timestamp: new Date()
        };
        setMessages(prev => [...prev, errorMessage]);
        setIsTyping(false);
      }, 500);
    }
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="min-h-screen bg-gradient-to-br from-purple-500 to-purple-700 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4 transition-colors duration-300">
        <div className="w-full max-width-4xl">
          <div className="bg-white dark:bg-gray-800 rounded-3xl shadow-2xl overflow-hidden flex flex-col h-[700px] transition-colors duration-300">

            {/* Header */}
            <div className="bg-gradient-to-r from-purple-600 to-purple-800 dark:from-gray-700 dark:to-gray-900 px-6 py-4 transition-colors duration-300">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-4">
                  <div className="w-12 h-12 bg-white/20 rounded-full flex items-center justify-center backdrop-blur">
                    <svg viewBox="0 0 24 24" fill="none" className="w-7 h-7 text-white">
                      <path d="M20 9V7C20 5.9 19.1 5 18 5H14V3H10V5H6C4.9 5 4 5.9 4 7V9C2.9 9 2 9.9 2 11V13C2 14.1 2.9 15 4 15V19C4 20.1 4.9 21 6 21H18C19.1 21 20 20.1 20 19V15C21.1 15 22 14.1 22 13V11C22 9.9 21.1 9 20 9ZM18 19H6V13H18V19ZM9 11C9 10.45 9.45 10 10 10C10.55 10 11 10.45 11 11C11 11.55 10.55 12 10 12C9.45 12 9 11.55 9 11ZM13 11C13 10.45 13.45 10 14 10C14.55 10 15 10.45 15 11C15 11.55 14.55 12 14 12C13.45 12 13 11.55 13 11Z" fill="currentColor"/>
                    </svg>
                  </div>
                  <div>
                    <h1 className="text-xl font-bold text-white">IAI Capstone</h1>
                    <p className="text-sm text-white/90">Batch-01  Section-14</p>
                  </div>
                </div>

                <div className="flex items-center gap-2">
                  <button
                    onClick={() => setShowHistory(!showHistory)}
                    className="p-2 hover:bg-white/10 rounded-lg transition-colors"
                    title="Chat History"
                  >
                    <History className="w-5 h-5 text-white" />
                  </button>
                  <button
                    onClick={() => setDarkMode(!darkMode)}
                    className="p-2 hover:bg-white/10 rounded-lg transition-colors"
                    title="Toggle Theme"
                  >
                    {darkMode ? <Sun className="w-5 h-5 text-white" /> : <Moon className="w-5 h-5 text-white" />}
                  </button>
                </div>
              </div>
            </div>

            {/* History Sidebar */}
            {showHistory && (
              <div className="absolute right-0 top-0 h-full w-80 bg-white dark:bg-gray-800 shadow-2xl z-50 border-l border-gray-200 dark:border-gray-700 transition-colors duration-300">
                <div className="flex items-center justify-between p-4 border-b border-gray-200 dark:border-gray-700">
                  <h3 className="font-semibold text-gray-900 dark:text-white">Chat History</h3>
                  <div className="flex gap-2">
                    {chatHistory.length > 0 && (
                      <button
                        onClick={clearHistory}
                        className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
                        title="Clear History"
                      >
                        <Trash2 className="w-4 h-4 text-gray-600 dark:text-gray-400" />
                      </button>
                    )}
                    <button
                      onClick={() => setShowHistory(false)}
                      className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
                    >
                      <X className="w-4 h-4 text-gray-600 dark:text-gray-400" />
                    </button>
                  </div>
                </div>
                <div className="overflow-y-auto h-full pb-20">
                  {chatHistory.length === 0 ? (
                    <div className="p-4 text-center text-gray-500 dark:text-gray-400">
                      No chat history yet
                    </div>
                  ) : (
                    chatHistory.map(item => (
                      <div
                        key={item.id}
                        onClick={() => loadHistoryItem(item)}
                        className="p-4 border-b border-gray-200 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-700 cursor-pointer transition-colors"
                      >
                        <p className="text-sm font-medium text-gray-900 dark:text-white truncate">{item.user}</p>
                        <p className="text-xs text-gray-500 dark:text-gray-400 mt-1 truncate">{item.bot}</p>
                        <p className="text-xs text-gray-400 dark:text-gray-500 mt-1">
                          {new Date(item.timestamp).toLocaleString()}
                        </p>
                      </div>
                    ))
                  )}
                </div>
              </div>
            )}

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-6 bg-gray-50 dark:bg-gray-900 transition-colors duration-300">
              {messages.length === 1 && (
                <div className="mb-6">
                  <p className="text-sm font-semibold text-gray-600 dark:text-gray-400 mb-3">Quick Actions:</p>
                  <div className="grid grid-cols-2 gap-3">
                    {quickActions.map((action, idx) => (
                      <button
                        key={idx}
                        onClick={() => handleQuickAction(action)}
                        className="p-3 bg-white dark:bg-gray-800 border-2 border-gray-200 dark:border-gray-700 rounded-xl text-gray-700 dark:text-gray-300 text-sm font-medium hover:border-purple-500 dark:hover:border-purple-600 hover:text-purple-600 dark:hover:text-purple-400 transition-all hover:-translate-y-0.5"
                      >
                        {action}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {messages.map((msg, idx) => (
                <div key={idx} className={`flex gap-3 mb-6 ${msg.sender === "user" ? "flex-row-reverse" : ""}`}>
                  <div className={`w-9 h-9 rounded-full flex items-center justify-center flex-shrink-0 ${
                    msg.sender === "bot" 
                      ? "bg-gradient-to-br from-purple-600 to-purple-800 dark:from-purple-700 dark:to-purple-900" 
                      : "bg-gradient-to-br from-pink-500 to-red-500"
                  }`}>
                    <svg viewBox="0 0 24 24" fill="none" className="w-5 h-5 text-white">
                      {msg.sender === "bot" ? (
                        <path d="M20 9V7C20 5.9 19.1 5 18 5H14V3H10V5H6C4.9 5 4 5.9 4 7V9C2.9 9 2 9.9 2 11V13C2 14.1 2.9 15 4 15V19C4 20.1 4.9 21 6 21H18C19.1 21 20 20.1 20 19V15C21.1 15 22 14.1 22 13V11C22 9.9 21.1 9 20 9ZM18 19H6V13H18V19ZM9 11C9 10.45 9.45 10 10 10C10.55 10 11 10.45 11 11C11 11.55 10.55 12 10 12C9.45 12 9 11.55 9 11ZM13 11C13 10.45 13.45 10 14 10C14.55 10 15 10.45 15 11C15 11.55 14.55 12 14 12C13.45 12 13 11.55 13 11Z" fill="currentColor"/>
                      ) : (
                        <path d="M12 12C14.21 12 16 10.21 16 8C16 5.79 14.21 4 12 4C9.79 4 8 5.79 8 8C8 10.21 9.79 12 12 12ZM12 14C9.33 14 4 15.34 4 18V20H20V18C20 15.34 14.67 14 12 14Z" fill="currentColor"/>
                      )}
                    </svg>
                  </div>
                  <div className="flex-1 max-w-[70%]">
                    <div className={`rounded-2xl p-4 ${
                      msg.sender === "bot"
                        ? "bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700"
                        : "bg-gradient-to-r from-purple-600 to-purple-800 dark:from-purple-700 dark:to-purple-900 text-white"
                    } transition-colors duration-300`}>
                      <p className="text-sm leading-relaxed whitespace-pre-wrap">{msg.text}</p>
                    </div>
                    <p className={`text-xs mt-1 ${msg.sender === "user" ? "text-right" : ""} text-gray-500 dark:text-gray-400`}>
                      {msg.timestamp.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' })}
                    </p>
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="flex gap-3 mb-6">
                  <div className="w-9 h-9 rounded-full flex items-center justify-center flex-shrink-0 bg-gradient-to-br from-purple-600 to-purple-800">
                    <svg viewBox="0 0 24 24" fill="none" className="w-5 h-5 text-white">
                      <path d="M20 9V7C20 5.9 19.1 5 18 5H14V3H10V5H6C4.9 5 4 5.9 4 7V9C2.9 9 2 9.9 2 11V13C2 14.1 2.9 15 4 15V19C4 20.1 4.9 21 6 21H18C19.1 21 20 20.1 20 19V15C21.1 15 22 14.1 22 13V11C22 9.9 21.1 9 20 9ZM18 19H6V13H18V19ZM9 11C9 10.45 9.45 10 10 10C10.55 10 11 10.45 11 11C11 11.55 10.55 12 10 12C9.45 12 9 11.55 9 11ZM13 11C13 10.45 13.45 10 14 10C14.55 10 15 10.45 15 11C15 11.55 14.55 12 14 12C13.45 12 13 11.55 13 11Z" fill="currentColor"/>
                    </svg>
                  </div>
                  <div className="bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-2xl p-4 transition-colors duration-300">
                    <div className="flex gap-1">
                      <span className="w-2 h-2 bg-gray-400 dark:bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '0s' }}></span>
                      <span className="w-2 h-2 bg-gray-400 dark:bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></span>
                      <span className="w-2 h-2 bg-gray-400 dark:bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></span>
                    </div>
                  </div>
                </div>
              )}

              <div ref={messagesEndRef} />
            </div>

            {/* Input */}
            <div className="p-4 bg-white dark:bg-gray-800 border-t border-gray-200 dark:border-gray-700 transition-colors duration-300">
              <div className="flex gap-3 items-center bg-gray-100 dark:bg-gray-900 rounded-2xl p-2 border-2 border-gray-200 dark:border-gray-700 focus-within:border-purple-500 dark:focus-within:border-purple-600 transition-all">
                <input
                  type="text"
                  value={input}
                  placeholder="Type your message..."
                  onChange={(e) => setInput(e.target.value)}
                  onKeyDown={handleKeyPress}
                  disabled={isTyping}
                  className="flex-1 bg-transparent px-3 py-2 outline-none text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
                />
                <button
                  onClick={handleSend}
                  disabled={!input.trim() || isTyping}
                  className="w-10 h-10 rounded-xl bg-gradient-to-r from-purple-600 to-purple-800 dark:from-purple-700 dark:to-purple-900 text-white flex items-center justify-center hover:scale-105 active:scale-95 disabled:opacity-40 disabled:cursor-not-allowed transition-all"
                >
                  <svg viewBox="0 0 24 24" fill="none" className="w-5 h-5">
                    <path d="M2.01 21L23 12L2.01 3L2 10L17 12L2 14L2.01 21Z" fill="currentColor"/>
                  </svg>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
