import React, { useState, useEffect, useRef } from "react";
import "./App.css"; // <- import this

// rest of the code with className like "chat-container", "message-user", etc.

export default function App() {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hello! I am your Campus Helpdesk Bot. How can I help you today?" },
  ]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { sender: "user", text: input };
    setMessages(prev => [...prev, userMsg]);
    const messageToSend = input;
    setInput("");

    try {
      const response = await fetch("http://127.0.0.1:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: messageToSend }),
      });

      const data = await response.json();
      const botMsg = { sender: "bot", text: data.reply };
      setMessages(prev => [...prev, botMsg]);
    } catch (error) {
      const botMsg = { sender: "bot", text: "Oops! Something went wrong." };
      setMessages(prev => [...prev, botMsg]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <div className="w-full max-w-xl bg-white rounded-2xl shadow-lg flex flex-col overflow-hidden">
        <div className="p-4 font-bold text-lg border-b border-gray-200 bg-blue-50 text-blue-700">
          Campus Helpdesk Chatbot
        </div>
        <div className="flex-1 p-4 overflow-y-auto h-[500px]">
          {messages.map((msg, idx) => (
            <div
              key={idx}
              className={`mb-3 flex ${msg.sender === "user" ? "justify-end" : "justify-start"}`}
            >
              <div
                className={`px-4 py-2 rounded-xl max-w-[70%] break-words ${
                  msg.sender === "user" ? "bg-blue-500 text-white" : "bg-gray-200 text-gray-800"
                }`}
              >
                {msg.text}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <div className="p-4 border-t border-gray-200 flex gap-2 bg-gray-50">
          <input
            className="flex-1 px-4 py-2 border rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Type your message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={handleKeyPress}
          />
          <button
            onClick={sendMessage}
            className="px-4 py-2 bg-blue-500 text-white rounded-xl hover:bg-blue-600 transition"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}
